mapper.insert:
INSERT INTO T_USER_SYS_AUTH (
       AUTH_ID
     , AUTH_NM
     , AUTH_DSC
     , RGST_ID
     , RGST_DT
     , MODI_ID
     , MODI_DT
) VALUES (
       #{authId}
     , #{authNm}
     , #{authDsc}
     , #{rgstId}
     , now()
     , #{modiId}
     , now()
)

mapper.update:
UPDATE
    T_USER_SYS_AUTH
SET
    AUTH_NM = #{authNm}
    , AUTH_DSC = #{authDsc}
    , MODI_ID = #{modiId}
    , MODI_DT = now()
WHERE
    AUTH_ID = #{authId}
