mapper.insert:
INSERT INTO T_META_TB_SPEC (
	MTS_ID
	, MTS_KO_NM
	, MTS_EN_NM
	, MTS_DEF
	, SRC_SYS
	, SRC_TB_NM
	, SRC_DB_CD
	, MTS_DSC
	, RGST_ID
	, RGST_DT
	, MODI_ID
	, MODI_DT
) VALUES (
	#{mtsId}
	, #{mtsKoNm}
	, #{mtsEnNm}
	, #{mtsDef}
	, #{srcSys}
	, #{srcTbNm}
	, #{srcDbCd}
	, #{mtsDsc}
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
)

mapper.update:
UPDATE
	T_META_TB_SPEC
SET
	MTS_KO_NM = #{mtsKoNm}
	, MTS_EN_NM = #{mtsEnNm}
	, MTS_DEF = #{mtsDef}
	, SRC_SYS = #{srcSys}
	, SRC_TB_NM = #{srcTbNm}
	, SRC_DB_CD = #{srcDbCd}
	, MTS_DSC = #{mtsDsc}
	, MODI_ID = #{modiId}
	, MODI_DT = now()
WHERE
	MTS_ID = #{mtsId}
