mapper.insert:
INSERT INTO
    T_BBS_DATA(
                DATA_ID
              , SJ
              , CN
              , VIEW_CNT
              , USE_YN
              , RGST_ID
              , RGST_DT
              , MODI_ID
              , MODI_DT
)
VALUES(
          #{dataId}
      , #{sj}
      , #{cn}
      , 0
      , #{useYn}
      , #{rgstId}
      , now()
      , #{modiId}
      , now()
      )

mapper.update:
UPDATE
    T_BBS_DATA
SET
    SJ      = #{sj}
  , CN      = #{cn}
  , USE_YN  = #{useYn}
  , MODI_ID = #{modiId}
  , MODI_DT = now()
WHERE
    DATA_ID = #{dataId}
