mapper.insert:
INSERT INTO T_DEPT(
       DEPT_CODE
     , DEPT_NM
     , UP_DEPT_CODE
     , ORD_SEQ
     , RGST_ID
     , RGST_DT
     , MODI_ID
     , MODI_DT
) VALUES (
       #{deptCode}
     , #{deptNm}
     , #{upDeptCode}
     , #{ordSeq}
     , #{rgstId}
     , now()
     , #{modiId}
     , now()
)

mapper.update:
        UPDATE T_DEPT
		   SET DEPT_NM = #{deptNm}
		     , UP_DEPT_CODE = #{upDeptCode}
		     , ORD_SEQ = #{ordSeq}
		     , MODI_ID = #{modiId}
		     , MODI_DT = now()
		 WHERE DEPT_CODE = #{deptCode}
