mapper.insert:
INSERT INTO T_USER_SYS_MENU(
       MENU_ID
     , UP_MENU_ID
     , MENU_NM
     , MENU_URL
     , MENU_DSC
     , ORD_SEQ
     , MENU_SE
     , MENU_ATTR
     , USE_YN
     , MENU_ICON
     , RGST_ID
     , RGST_DT
     , MODI_ID
     , MODI_DT
) VALUES (
       #{menuId}
     , #{upMenuId}
     , #{menuNm}
     , #{menuUrl}
     , #{menuDsc}
     , #{ordSeq}
     , #{menuSe}
     , #{menuAttr}
     , #{useYn}
     , #{menuIcon}
     , #{rgstId}
     , now()
     , #{modiId}
     , now()
)

mapper.update:
UPDATE T_USER_SYS_MENU
   SET MENU_ID = #{menuId}
     , UP_MENU_ID = #{upMenuId}
     , MENU_NM = #{menuNm}
     , MENU_URL = #{menuUrl}
     , MENU_DSC = #{menuDsc}
     , ORD_SEQ = #{ordSeq}
     , MENU_SE = #{menuSe}
     , MENU_ATTR = #{menuAttr}
     , USE_YN = #{useYn}
     , MENU_ICON = #{menuIcon}
     , MODI_ID = #{modiId}
     , MODI_DT = now()
 WHERE MENU_ID = #{menuId}
