mapper.insert:
INSERT INTO T_QCK_MENU_USER(
       USER_ID
     , MENU_ID
     , RGST_ID
     , RGST_DT
     , MODI_ID
     , MODI_DT
) VALUES (
       #{userId}
     , #{menuId}
     , #{rgstId}
     , now()
     , #{modiId}
     , now()
)

mapper.delete:
DELETE FROM T_QCK_MENU_USER
 WHERE USER_ID = #{userId}
   AND MENU_ID = #{menuId}
