mapper.insert:
INSERT INTO 
	T_BBS_NOTICE (
		  NOTICE_ID
		, SJ
		, CN
		, IMPORTANT_YN
		, ORD_SEQ
		, USE_YN
		, RGST_ID
		, RGST_DT
		, MODI_ID
		, MODI_DT
		, VIEW_CNT
		, POPUP_YN
		, START_DT
		, END_DT			
	) VALUES (
		  #{noticeId}
		, #{sj}
		, #{cn}
		, #{importantYn}
		, #{ordSeq}
		, #{useYn}
		, #{rgstId}
		, NOW()
		, #{modiId}
		, NOW()
		, 0
		, #{popupYn}
		, to_timestamp(#{startDt}||' 00:00:00','YYYY-MM-DD HH24:MI:SS')
		, to_timestamp(#{endDt}||' 23:59:59','YYYY-MM-DD HH24:MI:SS')
	)

mapper.update:
	UPDATE
		T_BBS_NOTICE
	SET
		  SJ = #{sj}
		, CN = #{cn}
		, IMPORTANT_YN = #{importantYn}
		, ORD_SEQ = #{ordSeq}
		, USE_YN = #{useYn}
		, MODI_ID = #{modiId}
		, MODI_DT = NOW()
		, POPUP_YN = #{popupYn}
		, START_DT = TO_TIMESTAMP(#{startDt}||' 00:00:00','YYYY-MM-DD HH24:MI:SS')
		, END_DT = TO_TIMESTAMP(#{endDt}||' 23:59:59','YYYY-MM-DD HH24:MI:SS')
   WHERE
		NOTICE_ID = #{noticeId}
